const catalog = staticData.catalog || {};
const models = Object.keys(catalog); // Obtiene lista: ["CM1100", "MDM4121", etc.]

// Si el cat√°logo est√° vac√≠o, advertimos
if (models.length === 0) {
  return [{ json: { 
    chatId: $json.chatId, 
    text: "‚ö†Ô∏è La memoria est√° vac√≠a. Por favor ejecuta /start para recargar.", 
    reply_markup: {} 
  }}];
}

// Construimos los botones (2 por fila)
const keyboard = [];
let row = [];

models.forEach((model, index) => {
  row.push({
    text: model, 
    callback_data: `${model}|START` // CLAVE: Esto inicia el flujo de esa m√°quina
  });

  if (row.length === 2 || index === models.length - 1) {
    keyboard.push(row);
    row = [];
  }
});

// Salida lista para tu nodo HTTP Request o Telegram
return [{
  json: {
    chatId: $json.chatId,
    text: "üè≠ *Cat√°logo VC999 Disponible*\n\nSelecciona la m√°quina que deseas cotizar:",
    reply_markup: {
      inline_keyboard: keyboard
    }
  }
}];

        const DEBUG = false;
const staticData = $getWorkflowStaticData('global');
        const catalog = staticData.catalog || {};
        staticData.cot = staticData.cot || {};

        const { chatId, text } = $json;

        // Helpers
        const money = (n) => {
          const x = Number(n || 0);
          return x.toLocaleString(undefined, { minimumFractionDigits: 0, maximumFractionDigits: 0 });
        };
        const toNum = (v) => {
          const s = String(v ?? '').replace(/[^0-9.\-]/g,'');
          const n = parseFloat(s);
          return Number.isFinite(n) ? n : 0;
        };
        const normText = (v) => String(v ?? '').trim().replace(/\s+/g, ' ');
        const optionKey = (label, value) => `${normText(label).toLowerCase()}|${normText(value ?? label).toLowerCase()}`;

        // Estado de usuario
        let userState = staticData.cot[chatId];

        // Detectar tipo de input
        const isCallback = typeof text === 'string' && text.includes('|');
        const isStartMachine = isCallback && text.endsWith('|START');
        const isOpt = isCallback && text.includes('|OPT|');

        // A) Selecci√≥n de m√°quina (inicio)
        if (isStartMachine) {
          const model = text.split('|')[0];
          const machine = catalog[model];
          if (!machine) {
            return [{ json: { chatId, text: "‚ö†Ô∏è Error: M√°quina no encontrada. Ejecuta /start.", reply_markup: {} } }];
          }

          userState = {
            stage: "config",          // config | await_name | await_email | done
            machine: model,
            stepIndex: 0,
            selections: [],
            totalPrice: toNum(machine.basePrice),
            customer: {}
          };
        }

        // B) Opci√≥n elegida
        if (isOpt) {
          const parts = text.split('|');
          const model = parts[0];
          const stepId = parts[2];
          const val = parts[3];
          const price = toNum(parts[4]);

          if (!userState || userState.machine !== model) {
            return [{ json: { chatId, text: "‚ö†Ô∏è Sesi√≥n expirada o modelo no coincide. Ejecuta /start.", reply_markup: {} } }];
          }

          userState.selections.push({ step: stepId, value: val, price });
          userState.totalPrice += price;
          userState.stepIndex += 1;
        }

        // C) Texto libre (Nombre / Correo) ‚Äî solo si ya terminamos pasos
        if (!isCallback && userState) {
          const msg = String(text || '').trim();

          if (userState.stage === "await_name") {
            userState.customer.name = msg;
            userState.stage = "await_email";
          } else if (userState.stage === "await_email") {
            userState.customer.email = msg;
            userState.stage = "done";
          }
        }

        // Validaci√≥n de m√°quina
        if (!userState) {
          return [{ json: { chatId, text: "Usa /start y selecciona una m√°quina para iniciar.", reply_markup: {} } }];
        }

        const machineConfig = catalog[userState.machine];
        if (!machineConfig) {
          return [{ json: { chatId, text: "‚ö†Ô∏è Cat√°logo no disponible. Ejecuta /start para recargar.", reply_markup: {} } }];
        }

        // D) Render de siguiente paso / cierre
        let response = {};
        let isFinished = false;

        if (userState.stage === "config") {
          const currentIndex = userState.stepIndex;

          if (currentIndex < machineConfig.steps.length) {
            const step = machineConfig.steps[currentIndex];
            const stepOptions = Array.isArray(step.options) ? step.options : [];

            if (stepOptions.length === 0) {
              const debugInfo = DEBUG ? ` [model=${userState.machine}, step=${step.id ?? currentIndex + 1}, steps=${machineConfig.steps.length}, options=${stepOptions.length}]` : '';

              response = {
                text: `‚ö†Ô∏è No hay opciones disponibles para el paso "${step.id ?? currentIndex + 1}". Usa /start para recargar el cat√°logo o contacta soporte.${debugInfo}`,
                reply_markup: {}
              };
            } else {
              const inline_keyboard = [];
              const seenOptions = new Set();

              const normalizeStepId = (stepId) => String(stepId ?? '')
                .toUpperCase()
                .trim()
                .replace(/[\s_]+/g, '');

              const STEP_FRIENDLY = {
                "LID": "Lid Height",
                "LIDSIZE": "Lid Height",
                "LIDHEIGHT": "Lid Height",
                "PUMP": "Pump Option",
                "PUMPOPTIONS": "Pump Option",
                "GAS": "Gas Flush",
                "GASFLUSH": "Gas Flush",
                "BIACTIVE": "Bi-Active Sealing",
                "BI ACTIVE": "Bi-Active Sealing",
                "PAS": "Positive Air Sealer",
                "POSITIVEAIRSEALER": "Positive Air Sealer"
              };

              const prettyLabel = (stepId, label, value) => {
                const rawLabel = String(label ?? value ?? '').trim();
                const normalizedId = normalizeStepId(stepId);

                if (rawLabel.length >= 18) return rawLabel;

                const friendly = STEP_FRIENDLY[normalizedId];
                if (!friendly) return rawLabel;

                const normalizeYesNoDisplay = (str) => {
                  const normalized = String(str ?? '').trim().toLowerCase();
                  if (["si", "s√≠"].includes(normalized)) return "S√≠";
                  if (["yes", "true", "y", "1"].includes(normalized)) return "Yes";
                  if (["no", "false", "0", "n"].includes(normalized)) return "No";
                  return str;
                };

                if (["LID", "LIDSIZE", "LIDHEIGHT"].includes(normalizedId)) {
                  const hasInch = /inch/i.test(rawLabel);
                  const seemsNumber = /^\s*\d+(\.\d+)?\s*$/.test(rawLabel);
                  const enriched = !hasInch && seemsNumber ? `${rawLabel} inch` : rawLabel;
                  return `${friendly}: ${enriched}`;
                }

                if (["GAS", "GASFLUSH", "BIACTIVE", "BI ACTIVE", "PAS", "POSITIVEAIRSEALER"].includes(normalizedId)) {
                  const display = normalizeYesNoDisplay(rawLabel);
                  return `${friendly}: ${display}`;
                }

                if (["PUMP", "PUMPOPTIONS"].includes(normalizedId)) {
                  return `${friendly}: ${rawLabel}`;
                }

                return `${friendly}: ${rawLabel}`;
              };

              stepOptions.forEach(opt => {
                const price = toNum(opt.price);
                const priceText = price > 0 ? ` (+$${money(price)})` : '';
                const value = opt.value ?? opt.label ?? 'NA';
                const key = optionKey(opt.label ?? value, value);
                if (seenOptions.has(key)) return;
                seenOptions.add(key);

                const baseLabel = opt.label ?? value;
                const display = prettyLabel(step.id, baseLabel, value);

                inline_keyboard.push([
                  {
                    text: `${display}${priceText}`,
                    callback_data: `${userState.machine}|OPT|${step.id}|${value}|${price}`
                  }
                ]);
              });

              response = {
                text:
`üõ†Ô∏è *${userState.machine}* (Paso ${currentIndex + 1}/${machineConfig.steps.length})
üí∞ Total: $${money(userState.totalPrice)} USD

${step.question ?? 'Selecciona una opci√≥n:'}`,
                reply_markup: { inline_keyboard }
              };
            }

          } else {
            // terminamos configuraciones -> pedir nombre
            userState.stage = "await_name";
            isFinished = true;

            response = {
              text:
`‚úÖ *Cotizaci√≥n lista*
Modelo: *${userState.machine}*
Total: *$${money(userState.totalPrice)} USD*

Escribe el *Nombre del Cliente*:`
            };
          }

        } else if (userState.stage === "await_name") {
          isFinished = true;
          response = { text: "üìù Ahora escribe el *correo del cliente*:" };

        } else if (userState.stage === "await_email") {
          isFinished = true;
          response = { text: "üìù Ahora escribe el *correo del cliente*:" };

        } else if (userState.stage === "done") {
          // JSON final (por ahora solo lo mostramos; luego lo mandas a PDF)
          const payload = {
            machine: userState.machine,
            basePrice: toNum(machineConfig.basePrice),
            totalPrice: toNum(userState.totalPrice),
            selections: userState.selections,
            customer: userState.customer
          };

          response = {
            text:
`üì¶ *JSON final listo para PDF*
\`\`\`json
${JSON.stringify(payload, null, 2)}
\`\`\``
          };
          isFinished = true;
        }

        // Persistir estado
        staticData.cot[chatId] = userState;

        return [{ json: { chatId, ...response, isFinished, userState } }];
