const update = $json;
let chatId, text = '';

// NORMALIZACIÓN ESTRICTA
if (update.message) {
  chatId = update.message.chat.id;
  text = update.message.text || '';
} else if (update.callback_query) {
  chatId = update.callback_query.message.chat.id;
  // AQUÍ ESTÁ LA CLAVE: Leemos el 'data' del botón
  text = update.callback_query.data || '';
}

return [{ json: { chatId, text, raw: update } }];

// CÓDIGO ORIGINAL (RESTAURAR DESPUÉS DE PROBAR)
const data = $getWorkflowStaticData('global');
const catalog = data.catalog;
let memoryEmpty = true;

if (catalog && Object.keys(catalog).length > 0) {
  let hasOptions = false;

  for (const model of Object.values(catalog)) {
    if (!model || !Array.isArray(model.steps)) continue;
    for (const step of model.steps) {
      if (!step || !Array.isArray(step.options)) continue;
      if (step.options.length > 0) {
        hasOptions = true;
        break;
      }
    }
    if (hasOptions) break;
  }

  memoryEmpty = !hasOptions;
}

const items = $input.all();
return items.map(item => ({ json: { ...item.json, memoryEmpty } }));

// --- CARGA DATA-DRIVEN DESDE GOOGLE SHEETS (DB_Maquinas + DB_Opciones)
// Fuentes: nodos "Leer DB_Maquinas" y "Leer DB_Opciones"
const toNum = (v) => {
  if (v === null || v === undefined) return 0;
  const s = String(v).replace(/[^0-9.\-]/g, '');
  const n = parseFloat(s);
  return Number.isFinite(n) ? n : 0;
};
const toInt = (v, d=0) => {
  const n = parseInt(String(v), 10);
  return Number.isFinite(n) ? n : d;
};

// getter flexible (tolerante a sinónimos/ mayúsculas / espacios)
const normKey = (k) => String(k || '').toLowerCase().trim().replace(/\s+/g,'').replace(/[_\-]/g,'');
const getVal = (obj, terms) => {
  if (!obj) return undefined;
  const keys = Object.keys(obj);
  const map = new Map(keys.map(k => [normKey(k), k]));
  for (const t of terms) {
    const nk = normKey(t);
    if (map.has(nk)) return obj[map.get(nk)];
  }
  for (const t of terms) {
    const nk = normKey(t);
    const partial = keys.find(k => normKey(k).includes(nk));
    if (partial) return obj[partial];
  }
  return undefined;
};

const norm = (v) => String(v ?? '').trim().replace(/\s+/g, ' ');
const normLower = (v) => norm(v).toLowerCase();
const optionSeen = new WeakMap();

// Nota: en n8n, el nombre exacto de los nodos importa:
const maquinasRows = $items("Leer DB_Maquinas").map(i => i.json);
const opcionesRows = $items("Leer DB_Opciones").map(i => i.json);

const catalog = {};

// 1) Máquinas
for (const row of maquinasRows) {
  const modelo = getVal(row, ["modelo","model","id","maquina_id"]);
  if (!modelo) continue;

  const nombre = getVal(row, ["nombre","name","descripcion","description"]) ?? modelo;
  const basePrice = toNum(getVal(row, ["precio_base","base_price","price","precio","costo"]));
  const plantilla = getVal(row, ["plantilla","template","docx","archivo"]);

  catalog[String(modelo)] = {
    id: String(modelo),
    name: String(nombre),
    basePrice,
    plantilla: plantilla ? String(plantilla) : undefined,
    steps: []
  };
}

// 2) Opciones / Pasos
for (const row of opcionesRows) {
  const modelo = getVal(row, ["modelo","model","maquina","maquina_id"]);
  if (!modelo) continue;
  const m = catalog[String(modelo)];
  if (!m) continue;

  const pasoId = getVal(row, ["paso_id","step_id","paso","step"]);
  const pregunta = getVal(row, ["pregunta","paso_pregunta","question","texto"]);
  const orden = toInt(getVal(row, ["paso_orden","order","orden"]), 0);

  if (!pasoId) continue;

  let step = m.steps.find(s => s.id === String(pasoId));
  if (!step) {
    const questionStr = norm(pregunta);
    step = { id: String(pasoId), question: questionStr || 'Selecciona una opción', order: orden, options: [] };
    m.steps.push(step);
  }

  const rawLabel = getVal(row, ["opcion_label","label","etiqueta","nombre_opcion","opcion"]) || getVal(row, ["opcion label","opcion_label","opcionlabel"]);
  const rawValue = getVal(row, ["opcion_valor","value","valor","code","key"]) || rawLabel;
  const price = toNum(getVal(row, ["opcion_precio","price","precio","costo","extra"]));

  const labelStr = norm(rawLabel || rawValue);
  let valueStr = norm(rawValue || rawLabel || labelStr);
  if (labelStr === '') continue;

  if (valueStr === '') valueStr = labelStr;

  const key = `${normLower(labelStr)}|${normLower(valueStr)}`;
  let seenForStep = optionSeen.get(step);
  if (!seenForStep) {
    seenForStep = new Set();
    optionSeen.set(step, seenForStep);
  }
  if (seenForStep.has(key)) continue;
  seenForStep.add(key);

  step.options.push({
    label: labelStr,
    value: valueStr,
    price
  });
}

// 3) Ordenar pasos y (opcional) opciones
for (const mid of Object.keys(catalog)) {
  catalog[mid].steps.sort((a,b) => (a.order ?? 0) - (b.order ?? 0));
}

const input = $input.first().json;
const staticData = $getWorkflowStaticData('global');
staticData.catalog = catalog;

return [{ json: { ...input, catalogLoaded: true, models: Object.keys(catalog).length } }];

// --- ROUTER MAESTRO (Callback vs Texto) ---
// Entrada: Normalize Telegram => { chatId, text, raw }
const { chatId, text, raw } = $json;

const staticData = $getWorkflowStaticData('global');
staticData.cot = staticData.cot || {};
const hasSession = !!staticData.cot?.[chatId];

// Defaults
let action = null;

const setMode = (mode) => {
  if (!mode) return;
  const current = staticData.cot[chatId] || {};
  staticData.cot[chatId] = { ...current, mode: mode.toUpperCase() };
};

// 1) /start o comandos
if (raw?.message?.text && raw.message.text.startsWith('/')) {
  action = 'START';
  staticData.cot[chatId] = {}; // limpiamos sesión previa
}
// 2) Botones (callback_data)
else if (raw?.callback_query?.data) {
  const data = raw.callback_query.data;

  if (data === 'MENU|AVANZADA' || data === 'MODE|ADV') {
    setMode('ADV');
    action = 'MENU_SELECTION';
  } else if (data === 'MODE|FAST') {
    setMode('FAST');
    action = 'MENU_SELECTION';
  } else {
    // Formatos esperados:
    //   MODEL|START
    //   MODEL|OPT|STEP|VALUE|PRICE
    action = 'COTIZACION_FLOW';
  }
}
// 3) Texto libre (nombre/correo) cuando ya existe sesión
else if (raw?.message?.text && !raw.message.text.startsWith('/') && hasSession) {
  action = 'COTIZACION_FLOW';
}

return [{ json: { chatId, text, action } }];

// --- GENERADOR DE MENÚ DINÁMICO ---
const staticData = $getWorkflowStaticData('global');
const catalog = staticData.catalog || {};
const models = Object.keys(catalog); // Obtiene lista: ["CM1100", "MDM4121", etc.]

// Si el catálogo está vacío, advertimos
if (models.length === 0) {
  return [{ json: { 
    chatId: $json.chatId, 
    text: "⚠️ La memoria está vacía. Por favor ejecuta /start para recargar.", 
    reply_markup: {} 
  }}];
}

// Construimos los botones (2 por fila)
const keyboard = [];
let row = [];

models.forEach((model, index) => {
  row.push({
    text: model, 
    callback_data: `${model}|START` // CLAVE: Esto inicia el flujo de esa máquina
  });

  if (row.length === 2 || index === models.length - 1) {
    keyboard.push(row);
    row = [];
  }
});

// Salida lista para tu nodo HTTP Request o Telegram
return [{
  json: {
    chatId: $json.chatId,
    text: "🏭 *Catálogo VC999 Disponible*\n\nSelecciona la máquina que deseas cotizar:",
    reply_markup: {
      inline_keyboard: keyboard
    }
  }
}];

const DEBUG = false;
const staticData = $getWorkflowStaticData('global');
const catalog = staticData.catalog || {};
staticData.cot = staticData.cot || {};

const { chatId, text } = $json;

// Helpers
const money = (n) => {
  const x = Number(n || 0);
  return x.toLocaleString(undefined, { minimumFractionDigits: 0, maximumFractionDigits: 0 });
};
const toNum = (v) => {
  const s = String(v ?? '').replace(/[^0-9.\-]/g, '');
  const n = parseFloat(s);
  return Number.isFinite(n) ? n : 0;
};
const normText = (v) => String(v ?? '').trim().replace(/\s+/g, ' ');
const optionKey = (label, value) => `${normText(label).toLowerCase()}|${normText(value ?? label).toLowerCase()}`;
const isGenericQuestion = (q) => {
  const normalized = normText(q).toLowerCase();
  if (!normalized) return true;

  const generics = [
    'selecciona una opción',
    'select an option',
    'choose an option'
  ];

  return normalized.length < 4 || generics.some((phrase) => normalized.includes(phrase));
};
const humanizeStepId = (stepId) => {
  const safe = String(stepId ?? '').trim();
  const withSpaces = safe
    .replace(/[_-]+/g, ' ')
    .replace(/([a-z])([A-Z])/g, '$1 $2')
    .trim();

  if (!withSpaces) return 'Paso';

  return withSpaces
    .split(' ')
    .filter(Boolean)
    .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
    .join(' ');
};
const resolveMode = (state) => (state?.mode || 'ADV').toUpperCase();
const formatSelections = (selections) => {
  if (!Array.isArray(selections) || selections.length === 0) return ['- Sin opciones'];

  return selections.map((sel) => {
    const stepLabel = humanizeStepId(sel.step ?? 'Paso');
    const priceText = toNum(sel.price) > 0 ? ` (+$${money(sel.price)} USD)` : ' (+$0 USD)';
    const valueText = sel.value ?? 'N/A';
    return `- ${stepLabel}: ${valueText}${priceText}`;
  });
};

// Estado de usuario
let userState = staticData.cot[chatId];

// Detectar tipo de input
const isCallback = typeof text === 'string' && text.includes('|');
const isStartMachine = isCallback && text.endsWith('|START');
const isOpt = isCallback && text.includes('|OPT|');

let fastIntroResponse = null;

// A) Selección de máquina (inicio)
if (isStartMachine) {
  const model = text.split('|')[0];
  const machine = catalog[model];
  if (!machine) {
    return [{ json: { chatId, text: "⚠️ Error: Máquina no encontrada. Ejecuta /start.", reply_markup: {} } }];
  }

  const mode = resolveMode(userState);

  userState = {
    mode,
    stage: mode === 'FAST' ? 'await_customer_name' : 'config',          // config | await_customer_name | await_customer_email | done
    machine: model,
    stepIndex: 0,
    selections: [],
    totalPrice: toNum(machine.basePrice),
    customer: {},
  };

  if (mode === 'FAST') {
    const steps = Array.isArray(machine.steps) ? machine.steps : [];
    const selections = [];
    let totalPrice = toNum(machine.basePrice);
    const warnings = [];

    steps.forEach((step, idx) => {
      const options = Array.isArray(step?.options) ? step.options : [];
      if (options.length === 0) {
        warnings.push(`Paso ${step?.id ?? idx + 1} sin opciones, se omitió.`);
        return;
      }

      const defaultOpt = options.find((opt) => (String(opt.label ?? '')).includes('(Default)')) || options[0];
      const chosenPrice = toNum(defaultOpt.price);

      selections.push({
        step: step?.id ?? `step_${idx + 1}`,
        value: defaultOpt.value ?? defaultOpt.label ?? 'NA',
        price: chosenPrice,
      });

      totalPrice += chosenPrice;
    });

    userState.selections = selections;
    userState.totalPrice = totalPrice;
    userState.stepIndex = steps.length;

    const warningText = warnings.length ? `\n⚠️ ${warnings.join(' ')}` : '';
    fastIntroResponse = {
      text: `⚡️ *Modo Rápido activado*\nSe seleccionaron las opciones por defecto. Escribe el *nombre del cliente*.${warningText}`,
    };
  }
}

// B) Opción elegida
if (isOpt) {
  const parts = text.split('|');
  const model = parts[0];
  const stepId = parts[2];
  const val = parts[3];
  const price = toNum(parts[4]);

  if (!userState || userState.machine !== model) {
    return [{ json: { chatId, text: "⚠️ Sesión expirada o modelo no coincide. Ejecuta /start.", reply_markup: {} } }];
  }

  if (userState.mode === 'FAST') {
    return [{ json: { chatId, text: "⚡️ Estás en Modo Rápido. Las opciones ya fueron auto-seleccionadas. Envía el nombre del cliente.", reply_markup: {} } }];
  }

  userState.selections.push({ step: stepId, value: val, price });
  userState.totalPrice += price;
  userState.stepIndex += 1;
}

// C) Texto libre (Nombre / Correo) — solo si ya terminamos pasos
if (!isCallback && userState) {
  const msg = normText(text || '');

  if (userState.stage === "await_customer_name") {
    userState.customer.name = msg;
    userState.stage = "await_customer_email";

    staticData.cot[chatId] = userState;

    return [{ json: { chatId, text: "📝 Ahora escribe el *correo del cliente*:", reply_markup: {}, isFinished: true, userState } }];
  } else if (userState.stage === "await_customer_email") {
    userState.customer.email = msg;
    userState.stage = "done";

    const payload = {
      machine: userState.machine,
      basePrice: toNum(catalog[userState.machine]?.basePrice),
      totalPrice: toNum(userState.totalPrice),
      selections: userState.selections,
      customer: userState.customer
    };

    userState.payloadFinal = payload;
    staticData.cot[chatId] = userState;

    const optionLines = formatSelections(userState.selections).join('\n');
    const summaryText =
`🛠️ *Cotización ${userState.machine}*
*Máquina:* ${userState.machine}
*Base:* $${money(payload.basePrice)} USD
*Opciones:*
${optionLines}
*Total:* $${money(payload.totalPrice)} USD
*Cliente:* ${userState.customer.name || 'N/A'}
*Correo:* ${userState.customer.email || 'N/A'}
✅ Confirmo datos. Procediendo a generar y enviar PDF.`;

    return [{ json: { chatId, text: summaryText, reply_markup: {}, isFinished: true, userState, quotePayload: payload } }];
  }
}

// Validación de máquina
if (!userState) {
  return [{ json: { chatId, text: "Usa /start y selecciona una máquina para iniciar.", reply_markup: {} } }];
}

const machineConfig = catalog[userState.machine];
if (!machineConfig) {
  return [{ json: { chatId, text: "⚠️ Catálogo no disponible. Ejecuta /start para recargar.", reply_markup: {} } }];
}

// D) Render de siguiente paso / cierre
let response = fastIntroResponse || {};
let isFinished = !!fastIntroResponse;

if (!isFinished && userState.stage === "config") {
  const currentIndex = userState.stepIndex;

  if (currentIndex < machineConfig.steps.length) {
    const step = machineConfig.steps[currentIndex];
    const stepOptions = Array.isArray(step.options) ? step.options : [];

    if (stepOptions.length === 0) {
      const debugInfo = DEBUG ? ` [model=${userState.machine}, step=${step.id ?? currentIndex + 1}, steps=${machineConfig.steps.length}, options=${stepOptions.length}]` : '';

      response = {
        text: `⚠️ No hay opciones disponibles para el paso "${step.id ?? currentIndex + 1}". Usa /start para recargar el catálogo o contacta soporte.${debugInfo}`,
        reply_markup: {},
      };
    } else {
      const inline_keyboard = [];
      const seenOptions = new Set();

      const normalizeStepId = (stepId) => String(stepId ?? '')
        .toUpperCase()
        .trim()
        .replace(/[\s_]+/g, '');

      const STEP_FRIENDLY = {
        "LID": "Lid Height",
        "LIDSIZE": "Lid Height",
        "LIDHEIGHT": "Lid Height",
        "PUMP": "Pump Option",
        "PUMPOPTIONS": "Pump Option",
        "GAS": "Gas Flush",
        "GASFLUSH": "Gas Flush",
        "BIACTIVE": "Bi-Active Sealing",
        "BI ACTIVE": "Bi-Active Sealing",
        "PAS": "Positive Air Sealer",
        "POSITIVEAIRSEALER": "Positive Air Sealer"
      };

      const prettyLabel = (stepId, label, value) => {
        const rawLabel = String(label ?? value ?? '').trim();
        const normalizedId = normalizeStepId(stepId);

        if (rawLabel.length >= 18) return rawLabel;

        const friendly = STEP_FRIENDLY[normalizedId];
        if (!friendly) return rawLabel;

        const normalizeYesNoDisplay = (str) => {
          const normalized = String(str ?? '').trim().toLowerCase();
          if (["si", "sí"].includes(normalized)) return "Sí";
          if (["yes", "true", "y", "1"].includes(normalized)) return "Yes";
          if (["no", "false", "0", "n"].includes(normalized)) return "No";
          return str;
        };

        if (["LID", "LIDSIZE", "LIDHEIGHT"].includes(normalizedId)) {
          const hasInch = /inch/i.test(rawLabel);
          const seemsNumber = /^\s*\d+(\.\d+)?\s*$/.test(rawLabel);
          const enriched = !hasInch && seemsNumber ? `${rawLabel} inch` : rawLabel;
          return `${friendly}: ${enriched}`;
        }

        if (["GAS", "GASFLUSH", "BIACTIVE", "BI ACTIVE", "PAS", "POSITIVEAIRSEALER"].includes(normalizedId)) {
          const display = normalizeYesNoDisplay(rawLabel);
          return `${friendly}: ${display}`;
        }

        if (["PUMP", "PUMPOPTIONS"].includes(normalizedId)) {
          return `${friendly}: ${rawLabel}`;
        }

        return `${friendly}: ${rawLabel}`;
      };

      const questionText = normText(step.question);
      const stepTitle = isGenericQuestion(questionText)
        ? humanizeStepId(step.id ?? `Paso ${currentIndex + 1}`)
        : questionText;

      stepOptions.forEach(opt => {
        const price = toNum(opt.price);
        const priceText = price > 0 ? ` (+$${money(price)})` : '';
        const value = opt.value ?? opt.label ?? 'NA';
        const key = optionKey(opt.label ?? value, value);
        if (seenOptions.has(key)) return;
        seenOptions.add(key);

        const baseLabel = opt.label ?? value;
        const display = prettyLabel(step.id, baseLabel, value);

        inline_keyboard.push([
          {
            text: `${display}${priceText}`,
            callback_data: `${userState.machine}|OPT|${step.id}|${value}|${price}`
          }
        ]);
      });

      response = {
        text:
`🛠️ *${userState.machine}* (Paso ${currentIndex + 1}/${machineConfig.steps.length})
💰 Total: $${money(userState.totalPrice)} USD

Selecciona: *${stepTitle}*`,
        reply_markup: { inline_keyboard }
      };
    }

  } else {
    // terminamos configuraciones -> pedir nombre
    userState.stage = "await_customer_name";
    isFinished = true;

    response = {
      text:
`✅ *Cotización lista*
Modelo: *${userState.machine}*
Total: *$${money(userState.totalPrice)} USD*

Escribe el *NOMBRE del Cliente*:`
    };
  }

} else if (userState.stage === "await_customer_name") {
  isFinished = true;
  response = { text: "📝 Ahora escribe el *NOMBRE del cliente*:" };

} else if (userState.stage === "await_customer_email") {
  isFinished = true;
  response = { text: "📝 Ahora escribe el *correo del cliente*:" };

} else if (userState.stage === "done") {
  const payload = userState.payloadFinal || {
    machine: userState.machine,
    basePrice: toNum(machineConfig.basePrice),
    totalPrice: toNum(userState.totalPrice),
    selections: userState.selections,
    customer: userState.customer
  };

  const optionLines = formatSelections(payload.selections).join('\n');
  const summaryText =
`🛠️ *Cotización ${userState.machine}*
*Máquina:* ${userState.machine}
*Base:* $${money(payload.basePrice)} USD
*Opciones:*
${optionLines}
*Total:* $${money(payload.totalPrice)} USD
*Cliente:* ${payload.customer?.name || 'N/A'}
*Correo:* ${payload.customer?.email || 'N/A'}
✅ Confirmo datos. Procediendo a generar y enviar PDF.`;

  response = { text: summaryText, quotePayload: payload };
  isFinished = true;
}

// Persistir estado
staticData.cot[chatId] = userState;

return [{ json: { chatId, ...response, isFinished, userState } }];

const userState = $json.userState || {}

const selections = Array.isArray(userState.selections)
  ? userState.selections.map((sel) => ({
      paso: sel.step ?? sel.label ?? '',
      opcion: sel.value ?? sel.option ?? '',
      precio: Number(sel.price ?? 0),
    }))
  : [];

const fastapiBody = {
  modelo: userState.machine ?? '',
  nombre_cliente: userState.customer?.name ?? '',
  email: userState.customer?.email ?? '',
  precio_base: Number(userState.basePrice ?? userState.totalPrice ?? 0),
  precio_cambiado: Number(userState.totalPrice ?? 0),
  selecciones: selections,
};

return [{ json: { ...$json, ...fastapiBody, fastapiBody } }];
